pragma solidity ^0.5.0;

import "./AviumToken.sol";


contract AviumUnitSale is AviumToken {

    struct Unit {
      address from;
      address to;
      uint256 value;
    }

    Unit[] public units;



    function pendingPayment(address _to, uint256 _value) public {
      units.push(Unit(msg.sender, _to, _value));
    }

    function makePayment(address _from, address _to) public returns (bool success) {
      require(msg.sender == _from);
      uint total = 0;
      for(uint i = 0; i < units.length; i++){
        if(units[i].to == _to){
          total += units[i].value;
          units[i].value = 0;
        }
      }

      emit Transfer(msg.sender, _to, total);

      return true;
      selfdestruct(msg.sender);
    }

}
